import React from "react";
import styled from "styled-components";
import imgbg from "./assets/bg1.png";
import imgfeed from "./assets/img2.png";

const Header = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin-left: 150px;
  margin-right: 150px;
  margin-top: 21px;
`;

const Logo = styled.div`
display: flex;
justify-content: flex-start;
font-family: Nunito;
font-weight: bold;
font-size: 32px;
line-height: 38.4px;
text-align: left;
color: #918FB3;
`;

const Menu = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  margin-top: 10px;
`;

const Items = styled.div`
  display: flex;
  margin-right: 30px;
  font-family: Lato;
  font-weight: normal;
  font-size: 24px;
  line-height: 28.8px;
  text-align: left;
  color: #666;
  justify-content: center;
`;

const Body = styled.div`
  display: flex;
  flex-direction: column;
`;

const ImgBG = styled.div`
background-image: url(${imgbg});
width: 100%;
height:500px;
`;

const ImgText = styled.div`
display:flex;
flex-direction:column;
margin-top: 30px;
margin-left:30px;
`

const ImgText1 = styled.div`
font-family: Lato;
font-weight: normal;
font-size: 36px;
line-height: 72px;
text-align: left;
color: #666;
`

