font-family: Lato;
font-weight: normal;
font-size: 60px;
line-height: 72px;
text-align: left;
color: #666;
`

const BodyBG = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #918fb3;
  width: 100%;
`;

const Text = styled.div`
  font-size: 36px;
  font-family: Nunito;
  color: white;
  display: flex;
  margin: 100px;
`;

const Card = styled.div`
  display: flex;
  flex-direction: column;
  height: 300px;
  width: 500px;
  border: 1px solid white;
  font-size: 32px;
  font-family: Lato;
  color: white;
  justify-content: center;
  text-align: center;
  margin: 0px 0px 150px 0px;
`;

const CardHeader = styled.div`
  display: flex;
  flex-direction: row;
`;

const CardNumber = styled.div`
  border: 1px solid white;
  width: 67px;
  height: 67px;
`;

const CardTitle = styled.div`
  height: 67px;
  width: 500px;
  border-bottom: 1px solid white;
`;

const CardText = styled.div`
  height: 300px;
  width: 500px;
`;
const Feedback = styled.div`
  display: flex;
  flex-direction: row;
  margin-left: 100px;
`;

const FeedbackIMG = styled.img`
  width: 100%;
`;

const TextoFeed = styled.div`
  display: flex;
  flex-direction: column;
  margin-right: 326px;
`;

const TextoTitle = styled.div`
  font-family: Nunito;
  font-weight: bold;
  font-size: 32px;
  line-height: 38.4px;
  text-align: left;
  color: #666;
`;

const TextoBody = styled.div`
  font-family: Lato;
  font-weight: normal;
  font-size: 32px;
  line-height: 38.4px;
  text-align: right;
  color: #666;
`;

const FeedSub = styled.div`
  margin-top: 100px;
  display: flex;
  flex-direction: row;
`;

const EmailFeed = styled.input`
  width: 390px;
  height: 47px;
  border-radius: 8px;
  background: #fff;
  border: 1px solid #b7b7b7;
  font-family: Lato;
  font-weight: normal;
  font-style: italic;
  font-size: 32px;
  line-height: 38.4px;
  text-align: right;
  color: #666;
  opacity: 0.58;
`;

const EmailButton = styled.button`
  width: 125px;
  height: 67px;
  background: #9d65a4;
  border: 1px solid #b7b7b7;
`;

const Team = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
`;

const TeamTitle = styled.div`
  font-family: Nunito;
  font-weight: bold;
  font-size: 36px;
  line-height: 43.2px;
  text-align: center;
  color: #918fb3;
`;

const TeamCard = styled.div`
  display: flex;
  flex-direction: column;
  width: 330px;
  height: 316px;
  background: #e9f1f2;
  margin-right: 32px;
  margin-bottom: 24px;
  margin-left: 100px;
  margin-top: 32px;
  border: 1px solid #b7b7b7;
`;

const CardTeamTitle = styled.div`
  display: flex;
  font-family: Nunito;
  font-weight: bold;
  font-size: 30px;
  line-height: 36px;
  text-align: left;
  color: #666;
`;

const CardSubtitle = styled.div`
  display: flex;
  font-family: Nunito-Italic;
  font-weight: normal;
  font-size: 28px;
  line-height: 33.6px;
  text-align: left;
  color: #666;
`;

const App = () => {
  return (
    <div>
      <Header>
        <Logo>TesteInovação</Logo>
        <Menu>
          <Items>O desafio</Items>
          <Items>Recomendaçoes</Items>
          <Items>Feedback</Items>
          <Items>A equipe</Items>
          <Items>Comentários</Items>
        </Menu>
      </Header>
      <Body>
        <ImgBG>
        <ImgText>
        <ImgText1>Criamos produtos incríveis</ImgText1>
        <ImgText2>que vão impactar o mercado.</ImgText2>
        <ImgText3>Você está pronto para </ImgText3>
        <ImgText4>fazer parte desse time?</ImgText4>
        </ImgText>
        </ImgBG>
        <BodyBG>
          <Text>Recomendaçoes: </Text>
          <Card>
            <CardHeader>
              <CardNumber>1</CardNumber>
              <CardTitle>Organize seu tempo</CardTitle>
            </CardHeader>
            <CardText>
              Verifique quanto tempo você tem disponível para realizar o teste
              com eficiênciae então crie pequenas metas para manter o ritmo.
            </CardText>
          </Card>
        </BodyBG>
        <Feedback>
          <FeedbackIMG src={imgfeed} />
          <TextoFeed>
            <TextoTitle>Quer um Feedback?</TextoTitle>
            <TextoBody>
              Preencha seu email abaixo para receber o feedback da sua
              avaliação.
              <br />
              Estamos torcendo por você!
            </TextoBody>
            <FeedSub>
              <EmailFeed placeholder="Digite seu email aqui..." />
              <EmailButton />
            </FeedSub>
          </TextoFeed>
        </Feedback>
        <Team>
          <TeamTitle>A equipe</TeamTitle>
          <TeamCard>
            <CardTeamTitle>João Pereira, 30</CardTeamTitle>
            <CardSubtitle>Desenvolvedor</CardSubtitle>
          </TeamCard>
          <TeamCard>
            <CardTeamTitle>Branca Silva, 33</CardTeamTitle>
            <CardSubtitle>Analista de qualidade</CardSubtitle>
          </TeamCard>
          <TeamCard>
            <CardTeamTitle>Carlos Gomes, 46 </CardTeamTitle>
            <CardSubtitle>Desenvolvedor Fullstack</CardSubtitle>
          </TeamCard>
        </Team>
      </Body>
    </div>
  );
};

export default App;
