import React from "react";
import styled from "styled-components";
import imgbg from "./assets/bg1.png";

const Header = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin-left: 150px;
  margin-right: 150px;
  margin-top: 21px;
`;

const Logo = styled.div`
  display: flex;
  justify-content: flex-start;
  font-size: 32px;
  font-weight: bold;
  font-family: Nunito;
`;

const Menu = styled.div`
  display: flex;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  margin-top: 10px;
`;

const Items = styled.div`
  display: flex;
  margin-right: 30px;
  font-size: 24px;
  font-family: Lato;
  font-weight: regular;
  justify-content: center;
`;

const Body = styled.div`
  display: flex;
  flex-direction: column;
`;

const ImgBG = styled.img`
  width: 100%;
`;

const BodyBG = styled.div`
  display: flex;
  background-color: #918fb3;
  width: 100%;
`;

const Card = styled.div`
display:flex;
flex-direction: column;
height:300px
width:500px;
border: 1px solid white;
font-size:32px;
font-family: Lato;
color: white;
justify-content: center;
text-align:center;
`;

const CardNumber = styled.div`
border: 1px solid white;
width:67px;
height:67px;
`

const CardTitle = styled.div`
height:67px;
width:500px;
border:1px solid white;
`

const CardText = styled.div`
height:300px;
width:500px;
border:1px solid white;
`

const App = () => {
  return (
    <div>
      <Header>
        <Logo>TesteInovação</Logo>
        <Menu>
          <Items>O desafio</Items>
          <Items>Recomendaçoes</Items>
          <Items>Feedback</Items>
          <Items>A equipe</Items>
          <Items>Comentários</Items>
        </Menu>
      </Header>
      <Body>
        <ImgBG src={imgbg} />
        <BodyBG>
          <Card>
            <CardNumber>1</CardNumber>
            <CardTitle>Organize seu tempo</CardTitle>
            <CardText>Verifique quanto tempo você tem disponível para realizar o teste com
            eficiênciae então crie pequenas metas para manter o ritmo.</CardText>
          </Card>
        </BodyBG>
      </Body>
    </div>
  );
};

export default App;
