import React from "react";
import styled from "styled-components";
import imgbg from "./assets/bg1.png";
import imgfeed from "./assets/img2.png"

const Header = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin-left: 150px;
  margin-right: 150px;
  margin-top: 21px;
`;

const Logo = styled.div`
  display: flex;
  justify-content: flex-start;
  font-size: 32px;
  font-weight: bold;
  font-family: Nunito;
`;

const Menu = styled.div`
  display: flex;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  margin-top: 10px;
`;

const Items = styled.div`
  display: flex;
  margin-right: 30px;
  font-size: 24px;
  font-family: Lato;
  font-weight: regular;
  justify-content: center;
`;

const Body = styled.div`
  display: flex;
  flex-direction: column;
`;

const ImgBG = styled.img`
  width: 100%;
`;

const BodyBG = styled.div`
  display: flex;
  flex-direction:column;
  background-color: #918fb3;
  width: 100%;
`;

const Text = styled.div`
font-size: 36px;
font-family:Nunito;
color:white;
display:flex;
`

const Card = styled.div`
display:flex;
flex-direction: column;
height:300px;
width:500px;
border: 1px solid white;
font-size:32px;
font-family: Lato;
color: white;
justify-content: center;
text-align:center;
`;

const CardHeader = styled.div`
display:flex;
flex-direction:row;
`

const CardNumber = styled.div`
border: 1px solid white;
width:67px;
height:67px;
`

const CardTitle = styled.div`
height:67px;
width:500px;
border-bottom: 1px solid white;
`

const CardText = styled.div`
height:300px;
width:500px;
`
const Feedback = styled.div`
display:flex;
flex-direction:row;
margin-left:100px;
margin-right:326px;
`

const FeedbackIMG = styled.img`
display:flex;
width:964px;
height:572px;
`

const TextoFeed = styled.div`
display:flex;
flex-direction:column;
`

const TextoTitle = styled.div`
font-family: Nunito;
font-weight: bold;
font-size: 32px;
line-height: 38.4px;
text-align: left;
color: #666;
`;

const TextoBody = styled.div`
font-family: Lato;
font-weight: normal;
font-size: 32px;
line-height: 38.4px;
text-align: right;
color: #666;
`;

const FeedSub = styled.div`
display:flex;
flex-direction:row;
`

const EmailFeed = styled.input`
width: 690px;
height: 67px;
border-radius: 8px;
background: #fff;
border: 1px solid #b7b7b7;
font-family: Lato;
font-weight: normal;
font-style: italic;
font-size: 32px;
line-height: 38.4px;
text-align: right;
color: #666;
opacity: 0.58;
`

const EmailButton = styled.button`
width: 225px;
height: 126px;
background: #9d65a4;
border: 1px solid #b7b7b7;
`

const App = () => {
  return (
    <div>
      <Header>
        <Logo>TesteInovação</Logo>
        <Menu>
          <Items>O desafio</Items>
          <Items>Recomendaçoes</Items>
          <Items>Feedback</Items>
          <Items>A equipe</Items>
          <Items>Comentários</Items>
        </Menu>
      </Header>
      <Body>
        <ImgBG src={imgbg} />
        <BodyBG>
          <Text>Recomendaçoes: </Text>
          <Card>
            <CardHeader>
            <CardNumber>1</CardNumber>
            <CardTitle>Organize seu tempo</CardTitle>
            </CardHeader>
            <CardText>Verifique quanto tempo você tem disponível para realizar o teste com
            eficiênciae então crie pequenas metas para manter o ritmo.</CardText>
          </Card>
        </BodyBG>
        <Feedback>
          <FeedbackIMG src={imgfeed} />
          <TextoFeed>
          <TextoTitle>
            Quer um Feedback?
          </TextoTitle>
          <TextoBody>
          Preencha seu email abaixo para receber
o feedback da sua avaliação.<br/>
Estamos torcendo por você!
          </TextoBody>
          <FeedSub>
          <EmailFeed placeholder="Digite seu email aqui..." /><EmailButton />
          </FeedSub>
          </TextoFeed>
        </Feedback>
      </Body>
    </div>
  );
};

export default App;
